class Smudge{constructor(h,t,i,s){this.xoff=random(1e6),this.yoff=random(1e6),this.woff1=random(1e6),this.aoff=random(1e6),this.rdnX=h,this.rdnY=t,this.rdnW1=i,this.mapXLow=3*-width,this.mapXHigh=3*width,this.mapYLow=3*-height,this.mapYHigh=3*height,this.alpha=int(random(0,50)),this.hue=hue(s),this.sat=saturation(s),this.bri=brightness(s),this.xoffInc=width/1e7,this.yoffInc=width/1e7,this.woff1Inc=width/4e3,this.aoffInc=width/4e5,this.maxWidth=height/1e3,this.minWidth=height/5e3,this.x=h,this.y=t,this.w=map(noise(this.woff1),0,1,this.minWidth,this.maxWidth,!0),this.offset=this.w/2}display(){this.updateValues(),noStroke(),[{x:this.x-this.offset,y:this.y-this.offset,w:this.w,h:this.w,col:[this.hue,0,0,this.alpha/2]},{x:this.x-this.offset,y:this.y+this.offset,w:this.w,h:this.w,col:[0,100,100,this.alpha]},{x:this.x+this.offset,y:this.y-this.offset,w:this.w,h:this.w,col:[200,100,100,this.alpha]},{x:this.x+this.offset,y:this.y+this.offset,w:1.25*this.w,h:1.25*this.w,col:[this.hue,0,100,this.alpha]}].forEach((({x:h,y:t,w:i,h:s,col:f})=>{fill(...f),ellipse(h,t,i,s)}))}updateValues(){this.xoff+=this.xoffInc,this.yoff+=this.yoffInc,this.woff1+=this.woff1Inc,this.aoff+=this.aoffInc,this.w=map(noise(this.woff1),0,1,this.minWidth,this.maxWidth,!0),this.x=map(noise(this.xoff),0,1,this.mapXHigh,this.mapXLow,!0),this.y=map(noise(this.yoff),0,1,this.mapYHigh,this.mapYLow,!0),this.alpha=map(noise(this.woff1),.6,1,1,100,!0),this.offset=this.w/2}}